var assert = require('assert');
var scrape = require("./main.js");

describe('Sample Scrape of Hacker News', function () {
    'use strict';
    
    it('prints out the first 30 titles of the hacker news front page', function () {
        scrape('news.ycombinator.com', ['.title a'], function (error, titles) {
            if(error) throw error;
            console.log('hacker news titles\n');
            titles.forEach(function (element, idx) {
                console.log('  %d. %s', idx+1, element.html());
            });
        });
    });

    it('determines the top ranked article of hacker news at runtime', function () {
        scrape('news.ycombinator.com', ['.title a'], function (error, titles) {
            if(error) throw error;
            console.log('top ranked article is: ' + titles[0].html());
        });
    });
});